import React from "react";
import PageTitle from "../PageTitle";
import './Login.css';
import '../templates/input.js';
import '../templates/input.css';
import '../templates/Button.js';
import '../templates/Button.css';
import '../util/validators.js';
import { VALIDATOR_EMAIL } from "../util/validators.js";


function Login() {
  return (
    <div className="">
      <PageTitle title="Login or Sign Up" />
      <div className="login_box">
        <form action="">
          <Input
            element = "input"
            id = "email"
            type = 'email'
            label = 'E-mail'
            validators = {[VALIDATOR_EMAIL()]}
            errortext = "Please enter a valid email address"
          >
          </Input>
        </form>
      </div>

    </div>
  );
}

export default Login;
